vars.getElement:
function() {
  
  let w = 400;
  let h = 800;
 
  var container = new UIContainer();
  
  var pane = new UIContainer();
  container.addChild(pane);
  
  pane.x = 0;
  pane.y = 0;
  
    
  let volume = game.render.text('XP Multiplier', 'ingame-label')
  pane.addChild(volume);
  volume.y = 0;
  volume.x = 0;
  
  var audio = game.render.componentRaw('hud_counter', {w: 50, ref: game.session.data.settings, value: 'xpMulti', max: 5, min: 1});
  
  pane.addChild(audio);
  
  audio.y = 20;
  
  let loot = game.render.text('Loot Multiplier', 'ingame-label')
  pane.addChild(loot);
  loot.y = 0;
  loot.x = 190;
  
  var loot2 = game.render.componentRaw('hud_counter', {w: 50, ref: game.session.data.settings, value: 'lootMulti', max: 5, min: 1});
  
  pane.addChild(loot2);
  
  loot2.y = 20;
  loot2.x = 190;
  
  let cash = game.render.text('Stating Cash', 'ingame-label')
  pane.addChild(cash);
  cash.y = 70;
  cash.x = 0;
  
  var cash2 = game.render.componentRaw('hud_counter', {w: 240, ref: game.session.data.settings, value: 'startingCash', max: 10000, min: 10, multi: 100});
  
  pane.addChild(cash2);
  
  cash2.y = 90;
  cash2.x = 0;
  
  let booleans = [
    'trading',
  	'talking',
    'building',
    'crafting',
    'squads',
    'map',
    'split_ai'
  ];
  
  let descs = [
    'Trading allowed`nAbility to trade',
  	'Talking allowed`nAbility to talk',
    'Building allowed`nAbility to build',
    'Crafting allowed`nAbility to craft',
    'Squads allowed`nAbility to add members',
    'Map allowed`nAbility to open the map',
    'Split Fighting`nAI tries to fight fairly'
  ];
  
  let padding = 10;

  
  let offsetX = 0
  let offsetY = 50 + 20 + 20 + 60
  
  for(let i = 0; i < booleans.length; i++) {
	let value = booleans[i];
    let desc = descs[i];
    var setting = game.render.componentRaw('drawBoxPane', {moveable: false, w:w-50, h:75, showX: false});
    pane.addChild(setting);
    setting.y = offsetY;
    
    let text = game.render.text(desc, 'ingame-label')
    setting.addChild(text);
    text.x = 20;
    text.y = 20;
    
    
    
    let checkbox = game.render.component('hud_checkbox',
        {w: 100,
      	  value: value,
          isEnabled: function(){
            return game.session.data.settings[this.value];
          },
      	  onDisable: function() {
          	game.session.data.settings[this.value] = false;
      	  },
          onEnable: function() {
          	game.session.data.settings[this.value] = true;
      	  }
        });
    
    checkbox.y = 17.5;
    checkbox.x = setting.x + setting.width - 15 - checkbox.width;
    setting.addChild(checkbox);
    
    
    offsetY += setting.height + padding;
  }
 
  
  
  return container; 
}
