vars.getElement:
function(options) {

    if(!options) {
      options = new ComplexItem('worlditem_craftingbench');
      
      if (typeof options.onCreate == 'function') {
        options.onCreate({});
      }
      options.data.queue = {data: {}};
      
      options.data.queue.data.list = [
        {name: 'Super Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
        {name: 'Deep Shotgun', duration: 10000, startedTime:false},
      ]
    }


  	let w = options.w || 450;
  	let h = options.h || 450;
  
    var pane = new UIContainer();
  
    var column = new TickerContainer(options, function(item){   
      let page = 1;


      if(this.pager && this.pager.pages) {
        page = this.pager.pages.page;
      }

      let list = [];
      for(let i = 0; i < item.data.queue.data.list.length; i++) {
        let listItem = item.data.queue.data.list[i]
      	list.push(game.render.componentRaw('component_list_item_queue', {
          w: w, label: listItem.name,
          started: listItem.startedTime,
          duration: listItem.duration,
          queue: item.data.queue,
          queueIndex: i
        }));

      }
      
      this.pane = new UIPane({fullscreen:false, fill: '0x303030', padding:0, x: -10, y: 0, w: w+10, h:h});
      let pager = game.render.componentRaw('pager', {rowHeight: 50, ticks: true, page: page, width:w, items:list, itemsPerPage:7});

      pager.x = -5
      pager.y = 10;
      if(this.pager) {
		this.pager.destroy();
      }
      this.pager = pager;
      this.pane.addChild(pager)

      this.addChild(this.pane);

    }, 0.25);
  
    column.lastRecipe = 'whatever';

    pane.addChild(column);
  
  
  	return pane;
  
  }
