vars.getElement:
function(options) {

    if(!options) {
      options = {};
    }
  

  
    var pane = new UIContainer();
  	game.cover = new Sprite('sprite_frontmenu_bg');
	game.cover.alpha = 0.5;
  	game.cover.isInteractive();
    pane.addChild(game.cover);
   
    game.setTicker("cover", ()=>{
      if(game.cover) {
        game.cover.height = game.ui._VIEWPORT_BOTTOM;
        game.cover.width = game.ui._VIEWPORT_RIGHT;
        
        game.cover.logo.x = game.ui._VIEWPORT_RIGHT - game.cover.logo.width - 15;

      }
    });
  
  	let cooly = game.render.component('hud_makes_cool', 'cooly');
  	game.cover.cooly = cooly;
  	pane.addChild(cooly);
  
  	let logo = new Sprite('sprite_logo');
  	logo.y = 15;
  	logo.setScale(0.75);
  	game.cover.logo = logo;
  	pane.addChild(logo);
  
  	var x = 150;
  	var y = 150;
  	var pad = 50;
  	var btnType = 'hud_button';
  	
  
    if(options.close) {
      var btn = game.render.componentRaw(btnType, {name: 'Save Game'});
          pane.addChild(btn);
          btn.x = x;
          btn.y = y;
          y += pad;
          btn.isInteractive();
          btn.on('pointerup', function(){
            game.index.getIndex('open_components')['frontMenu'].destroy();
            game.saves.requestSaveWithName();
        });
    }
  
  if(!options.close) {
  	var btn = game.render.componentRaw(btnType, {name: 'New Game'});
  	pane.addChild(btn);
  	btn.x = x;
  	btn.y = y;
  	y += pad;
  	btn.pane = pane;
  	btn.isInteractive();
  	btn.on('pointerup', function(){
      let ui = game.render.componentRaw('hud_new_game', {title: 'New Game', type: 'new'}, 'hud_load');
        this.pane.addChild(ui);
        ui.x = 100;
        ui.y = 100;
    });
  }
    var btn = game.render.componentRaw(btnType, {name: 'Load'});
      pane.addChild(btn);
      btn.x = x;
      btn.y = y;
      y += pad;
  btn.pane = pane;
      btn.isInteractive();
      btn.on('pointerup', function(){
        let ui = game.render.componentRaw('hud_load', {title: 'Load Game', type: 'load'}, 'hud_load');
        this.pane.addChild(ui);
        ui.x = 100;
        ui.y = 100;
      });

    var btn = game.render.componentRaw(btnType, {name: 'Settings'});
      pane.addChild(btn);
      btn.x = x;
      btn.y = y;
      y += pad;
  btn.pane = pane;
      btn.isInteractive();
      btn.on('pointerup', function(){
        game.ui.open('panel_settings', {x:50, y:50});
      });
  
  if(!options.close) {
  var btn = game.render.componentRaw(btnType, {name: 'Level editor'});
      pane.addChild(btn);
  btn.pane = pane;
      btn.x = x;
      btn.y = y;
      y += pad;
      btn.isInteractive();
      btn.on('pointerup', function(){
        window.location.href="?noAudio=true&test=true&type=scene&unit=lvleditor&level=dev/test1&x=67&y=88&tt=y&map=live"
      });
  }

    var btn = game.render.componentRaw(btnType, {name: 'Exit'});
      pane.addChild(btn);
      btn.x = x;
  btn.pane = pane;
      btn.y = y;
      y += pad;
      btn.isInteractive();
      btn.on('pointerup', function(){
        var win = nw.Window.get();
        win.close();
    });
  
  	if(options.close) {
      var btn = game.render.componentRaw(btnType, {name: 'Close Menu'});
          pane.addChild(btn);
          btn.x = x;
      btn.pane = pane;
          btn.y = y;
          y += pad;
          btn.isInteractive();
          btn.on('pointerup', function(){
              game.index.getIndex('open_components')['frontMenu'].destroy();

        });
    }
  

  
  	return pane;
  }
