vars.getElement:
function(options) {

  return new TickerContainer({}, function () {


    let w = 100;
    let h = 800;

    let page = 1;

    let list = [
      game.render.component('hud_checkbox',
        {
          name: 'New Map',
          isEnabled: function () {
            return true;
          },
          onDisable: function () {
            game.grid.createNewMap();
          }
        }),
      game.render.component('hud_checkbox',
        {
          name: 'Save Map',
          isEnabled: function () {
            return true;
          },
          onDisable: function () {
            game.grid.saveOpenChunks();
            game.notify("Saved");
          },
          onEnable: function () {
            game.grid.saveOpenChunks();
            game.notify("Saved");
          }
        }),
        game.render.component('hud_checkbox',
        {
          name: 'Overwrite Map',
          isEnabled: function () {
            return true;
          },
          onDisable: function () {
            game.grid.overwriteOpenChunks();
          },
          onEnable: function () {
            game.grid.overwriteOpenChunks();
          }
        }),
      game.render.component('hud_checkbox',
        {
          name: 'Test Map',
          isEnabled: function () {
            return true;
          },
          onDisable: function () {
            game.grid.saveOpenChunks();
            setTimeout(function () {
              let x = Math.floor(Math.abs(game.mouseX / 64 / 10));
              let y = Math.floor(Math.abs(game.mouseY / 64 / 10));
              window.open("deaddesert.html?noAudio=true&test=true&type=scene&unit=lvltest&x=" + x + "&y=" + y + "&tt=y&map="+game.urlVar('map')+"&readOnly=true");
            }, 2500);
          },
          onEnable: function () {
            game.grid.saveOpenChunks();
            setTimeout(function () {
              let x = Math.floor(Math.abs(game.mouseX / 64 / 10));
              let y = Math.floor(Math.abs(game.mouseY / 64 / 10));
              window.open("deaddesert.html?noAudio=true&test=true&type=scene&unit=lvltest&x=" + x + "&y=" + y + "&tt=y&map="+game.urlVar('map')+"&readOnly=true");
            }, 2500);
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Tablet Mode',
          isEnabled: function () {
            return game.clientMode == 'tablet';
          },
          onDisable: function () {
            game.clientMode = 'client';
          },
          onEnable: function () {
            game.clientMode = 'tablet';
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Gen Trees',
          isEnabled: function () {
            return game.genTrees
          },
          onDisable: function () {
            game.genTrees = false;
          },
          onEnable: function () {
            game.genTrees = true;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Gen Grass',
          isEnabled: function () {
            return game.genGrass
          },
          onDisable: function () {
            game.genGrass = false;
          },
          onEnable: function () {
            game.genGrass = true;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Layer 1',
          isEnabled: function () {
            return (game.drawLayer == 1 || game.drawLayer == undefined)
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 1;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Layer 2',
          isEnabled: function () {
            return game.drawLayer == 2;
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 2;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Layer 3',
          isEnabled: function () {
            return game.drawLayer == 3;
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 3;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Layer 4',
          isEnabled: function () {
            return game.drawLayer == 4;
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 4;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Wall 5',
          isEnabled: function () {
            return game.drawLayer == 5;
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 5;
          }
        }),

      game.render.component('hud_checkbox',
        {
          name: 'Layer6',
          isEnabled: function () {
            return game.drawLayer == 6;
          },
          onDisable: function () {

          },
          onEnable: function () {
            game.drawLayer = 6;
          }
        }),



    ];

    var pane = new UIContainer({ fullscreen: false, fill: '0x303030', padding: 0, x: -10, y: 5, w: 330, h: h - 55 });
    let pager = game.render.componentRaw('pager', { ticks: true, page: page, width: 320, items: list, itemsPerPage: 10 });

    pager.x = -5
    pager.y = 10;

    pane.addChild(pager)
    this.addChild(pane);
  }, 0.5);
}
