vars.getElement:
function () {

    var pane = game.render.componentRaw('drawBoxPane', { showX: false, x: 0, y: 0, w: 200, h: 60 });

    let padding = 20;

    game.buildTools.stopCircle();
    game.circleMode = false;
    game.fillMode = false;


    var btn = 'btn_square';
    var icon = new Sprite(btn);
    icon.id = btn;
    game.index.addToIndex('level_editor_toggles', icon)
    icon.setScale(0.5)
    icon.x = 15;
    icon.y = 15;
    icon.alpha = 1;
    icon.isInteractive();
    icon.on('pointerup', function () {
        game.index.modifyAllInIndex('level_editor_toggles', { alpha: 0.25 });
        this.alpha = 1;
        game.levelEditorButtonMode = this.id.replace(/btn_/, '');
        game.buildTools.stopCircle();
        game.circleMode = false;
        game.fillMode = false;
    });
    pane.addChild(icon);

    var btn = 'btn_circle';
    var icon = new Sprite(btn);
    icon.id = btn;
    game.index.addToIndex('level_editor_toggles', icon)
    icon.setScale(0.5)
    icon.x = padding + icon.width;
    icon.y = 15;
    icon.alpha = 0.25;
    icon.isInteractive();
    icon.on('pointerup', function () {
        game.index.modifyAllInIndex('level_editor_toggles', { alpha: 0.25 });
        this.alpha = 1;
        game.levelEditorButtonMode = this.id.replace(/btn_/, '');

        game.buildTools.stopCircle();
        game.circleMode = true;
        game.fillMode = false;

    });
    pane.addChild(icon);

    var btn = 'btn_brush';
    var icon = new Sprite(btn);
    icon.id = btn;
    game.index.addToIndex('level_editor_toggles', icon)
    icon.setScale(0.5)
    icon.x = padding + icon.width * 2;
    icon.y = 15;
    icon.alpha = 0.25;
    icon.isInteractive();
    icon.on('pointerup', function () {
        game.index.modifyAllInIndex('level_editor_toggles', { alpha: 0.25 });
        this.alpha = 1;
        game.levelEditorButtonMode = this.id.replace(/btn_/, '');
        game.buildTools.stopCircle();
        game.circleMode = false;
        game.fillMode = false;

        game.buildTools.circle();
    });
    pane.addChild(icon);

    var btn = 'btn_fill';
    var icon = new Sprite(btn);
    icon.id = btn;
    game.index.addToIndex('level_editor_toggles', icon)
    icon.setScale(0.5)
    icon.x = padding + icon.width * 3;
    icon.y = 15;
    icon.alpha = 0.25;
    icon.isInteractive();
    icon.on('pointerup', function () {
        game.index.modifyAllInIndex('level_editor_toggles', { alpha: 0.25 });
        this.alpha = 1;
        game.levelEditorButtonMode = this.id.replace(/btn_/, '');
        game.buildTools.stopCircle();
        game.circleMode = false;
        game.fillMode = true;
    });
    pane.addChild(icon);

    return pane;
}
