vars.getElement:
function(options) {
  
  if(!options) {
	options = {};
  }
  
  
  var text = options.text || "Can't do that!";
  
  var w = 400;
  var h = 150;
  var pane = game.render.componentRaw('drawBoxPane', {title:'Notification', moveable: true, showX: true, w: w, h: h});
  

  
  
  var text = game.render.text(text, 'longerwhitedesc');
  text.x = pane.width/2
  text.y = pane.height/2
  text.anchor.set(0.55,1);
  pane.addChild(text);
  return pane;
  
}
