vars.getElement:
function(options) {
    if(!options) {
        options = {
            dialog: 'dialog_recruit_npc',
            callerObject: new LifeObject()
        }
    }
    console.log("Get dialog :" + options.dialog);

    console.log("Caller :", options.callerObject);

    var dialog = _BLUEPRINTS.DIALOGS[options.dialog];
    return game.render.component('game_dialog_window', { dialog: dialog, options: options }, 'game_dialog');
}
