vars.getElement:
function() {

  let onDown = function (stat, container, item, desc) {
    item.container = container;
    item.isInteractive();
    item.on('pointerover', function () {
      this.container.text = desc;
      this.container.stat = stat;
    });

    return item;
  }

  let container = game.render.componentRaw('drawBoxPane', { title: game.p.data.name+' Stats', moveable: true, w: 900, h: 420, showX: true });

  this.watch = game.p;
  
  let pane = new UIContainer();
  container.addChild(pane);

  pane.x = 20;
  pane.y = 20;

  let list = [

    onDown('savage', this, game.render.component('drawLabel', {
      name: 'Savage',
      value: this.watch.data.levels.savage.level,
    }), 'How experienced you are overall, more savage means more `nrespect and unique options in the game. `n`nLevel up savage with all actions'),

    onDown('melee', this, game.render.component('drawLabel', {
      y: 1,
      name: 'Melee',
      value: this.watch.data.levels.melee.level,
    }), 'How much damage you do with melee attacks `n`nLevel up melee by killing with fists or melee weapons'),

    onDown('ranged', this, game.render.component('drawLabel', {
      y: 2,
      name: 'Range',
      value: this.watch.data.levels.ranged.level,
    }), 'How much accuracy you have with ranged weapons `n`nLevel up by using ranged weapons'),
    onDown('toughness', this, game.render.component('drawLabel', {
      y: 3,
      name: 'Toughness',
      value: this.watch.data.levels.toughness.level,
    }), 'How much damage resistance you have `n`nLevel up by taking damage and healing'),

  ];

  let column = game.render.componentRaw('pager', { items: list, itemsPerPage: 4 });
  pane.addChild(column);

  let list2 = [


    onDown('strength', this, game.render.component('drawLabel', {
      y: 1,
      name: 'Strength',
      value: this.watch.data.levels.strength.level,
    }), 'Increases carrying weight as well as the ability to destroy `ncertain items`n`nLevel up by travelling with high weight'),

    onDown('athletics', this, game.render.component('drawLabel', {
      y: 2,
      name: 'Athletics',
      value: this.watch.data.levels.athletics.level,
    }), 'Increases running speed and chance to dodge melee attacks `n`nLevel up by running with low weight'),

    onDown('crafting', this, game.render.component('drawLabel', {
      name: 'Crafting',
      value: this.watch.data.levels.crafting.level,
    }), 'Increases the level of items you can craft also the`nresources gained from grinding.`n`nLevel up by crafting and grinding items'),

    onDown('intelligence', this, game.render.component('drawLabel', {
      y: 3,
      name: 'Intelligence',
      value: this.watch.data.levels.intelligence.level,
    }), 'Effects the speed of hacking and increases speech effectiveness`n`nLevel by hacking and talking')
  ];

  let column2 = game.render.componentRaw('pager', { items: list2, itemsPerPage: 4 });
  pane.addChild(column2);
  column2.x += column.width + 10;

  let leftPane = new UIPane({ fill: '0x212121', w: column2.x + column2.width, h: 160 });
  pane.addChild(leftPane);
  leftPane.y = column.height - 35


  this.text = 'Hover over stats to get more information';
  this.leftText = false;

  let leftText = new TickerContainer(this, function (pass) {

    let stat = pass.stat;
    
    let extra = "";
    if(stat) {
     let xpStart = pass.watch.data.levels[stat].xp-pass.watch.data.levels[stat].lastXP;
     let nextXP = pass.watch.data.levels[stat].nextXP-pass.watch.data.levels[stat].lastXP;
     let onePercent = nextXP / 100;
     let percent =  Math.ceil(xpStart / onePercent);
     extra = "`n`nProgress: "+Math.ceil(xpStart) +" / "+nextXP + " XP";
     let progressBar = game.render.componentRaw('statBar', {
      	value: xpStart,
        maxValue: nextXP,
       	fill: 0x009900,
        w: 510,
       	h: 30,
       	name: percent+"%"
      });
      
      this.addChild(progressBar);
      progressBar.y = 170;
    }
    
    let leftText = game.render.text(pass.text+extra, 'item-desc');
    this.addChild(leftText);
    leftText.x = 10;
    leftText.y = 10;
    

  }, 0.1);

  pane.addChild(leftText);
  leftText.y = 135;

  let rightPane = new UIPane({ fill: '0x212121', w: 340, h: 202 });
  pane.addChild(rightPane);
  rightPane.y = 0;
  rightPane.x = leftPane.width + 12.5;

  let rightText = game.render.text(
    'Rating: Petty Clone`n`nDays Survived: 10`nClones Killed: 124`nClones Converted: 2`nCodecs Read: 2/25`nItems Crafted: 25`nItems Grinded: 2`nSpeech Checks Passed: 1`nHacks completed: 12', 'item-desc');
  pane.addChild(rightText);
  rightText.x = 530;
  rightText.y = 10;

  return container;
}
