vars.getElement:
function() {
  
  let w = 800;
  let h = 800;
  
  var container = game.render.componentRaw('drawBoxPane', {title: 'Settings', moveable: false, w:w, h:h, showX: true});
  
  var pane = new UIContainer();
  container.addChild(pane);
  
  pane.x = 40;
  pane.y = 50;
  
  let booleans = [
    'showTut',
  	'particles',
    'lights',
    'bloom',
    'blood',
    'performance'
  ];
  
  let descs = [
    'Enables showing the tutorial',
  	'Particles`nEffects animations like gun shrapnel',
    'Advanced Lights`nDisable if slow framerate',
    'Advanced Bloom`nDisable if slow framerate',
    'Blood`nDisable if slow framerate or dislike',
    'Soft Loading`nEnable if game jerks between areas'
  ];
  
  let padding = 10;
  let note = game.render.text('Note: You may have to restart for changes to take effect', 'ingame-label')
  pane.addChild(note);
  
  
  let volume = game.render.text('Volume', 'ingame-label')
  pane.addChild(volume);
  volume.y = 50;
  volume.x = 5;
  
  var audio = game.render.componentRaw('hud_counter', {ref: game.settings, value: 'musicValue', max: 10, min: 0});
  
  pane.addChild(audio);
  
  audio.y = 80;
  
  let offsetX = pane.x;
  let offsetY = pane.y + 100 + padding;
  
  for(let i = 0; i < booleans.length; i++) {
	let value = booleans[i];
    let desc = descs[i];
    var setting = game.render.componentRaw('drawBoxPane', {moveable: false, w:700, h:75, showX: false});
    pane.addChild(setting);
    setting.y = offsetY;
    
    let text = game.render.text(desc, 'ingame-label')
    setting.addChild(text);
    text.x = 20;
    text.y = 20;
    
    
    
    let checkbox = game.render.component('hud_checkbox',
        {
      	  value: value,
          isEnabled: function(){
            return game.settings[this.value];
          },
      	  onDisable: function() {
          	game.settings[this.value] = false;
      	  },
          onEnable: function() {
          	game.settings[this.value] = true;
      	  }
        });
    
    checkbox.y = 17.5;
    checkbox.x = 520;
    setting.addChild(checkbox);
    
    
    offsetY += setting.height + padding;
  }
  
  return container; 
}
