events.onCreate:
function(furni){
  	

    var myLight = new Sprite('lightgreen_screenwhite', {anchor:'center'});
  	myLight.scale.x = 2.5;
  	myLight.scale.y = 2.5;
  	myLight.alpha = 0.2;
    myLight.x = 32;
    myLight.y = 32;

	myLight.filters = [game.render.filters.bloom]
  	furni.addChild(myLight);
  
  	var myLight = new Sprite('lightcookie_oval', {anchor:'center'});
  	myLight.scale.x = 4;
  	myLight.scale.y = 4;
    myLight.x = furni.x + 64;
    myLight.y = furni.y + 64;
	
  	//game.render.objectLayer.addChild(myLight);
  	game.render.lights.addChild(myLight);
  

  
  
}
