events.onCreate:
function(furni){
  	

    var myLight = new Sprite('effect_light_cookie_y', {anchor:'center'});
  	myLight.scale.x = 0.8;
  	myLight.scale.y = 0.8;
  	myLight.alpha = 0.3;
    myLight.x = furni.width/2;
    myLight.y = furni.height/2 - 25;

	myLight.filters = [game.render.filters.bloom]
  	furni.addChild(myLight);
  
  	var myLight = new Sprite('lightcookie_oval', {anchor:'center'});
  	myLight.scale.x = 2;
  	myLight.scale.y = 2;
    myLight.x = furni.x + 64;
    myLight.y = furni.y + 80;
	
  	//game.render.objectLayer.addChild(myLight);
  	game.render.lights.addChild(myLight);
  

  
  
}
