events.onEquipt:
function(life) {
  	game.render.spine.hackSpineSlot('head_down', life.spine, game.render.getSpriteTexture('blank'));
    game.render.spine.hackSpineSlot('head_right', life.spine, game.render.getSpriteTexture('blank'));
    game.render.spine.hackSpineSlot('head_up', life.spine, game.render.getSpriteTexture('blank'));
    game.render.spine.hackSpineSlot('hand', life.spine, game.render.getSpriteTexture('blank'));
  	game.render.spine.hackSpineSlot('hand2', life.spine, game.render.getSpriteTexture('blank'));
}
